"""Fonts from popular sources."""
import typing


def load_google_font(name: str) -> str:
    return """
    <link
        rel="stylesheet"
        href="https://fonts.googleapis.com/css?family={0}"
    ></link>
    """.format(
        name.replace(
            " ", "+"
        )
    )


def load_google_fonts(names: typing.List[str]) -> str:
    string = ""
    for name in names:
        thing = name + "|"
        string += thing
    return load_google_font(string)
