"""Gives you access to different popular web libraries."""


def jquery(version: str, slim: bool = False) -> str:
    return """
    <script
        src="https://code.jquery.com/jquery-{0}{1}.min.js"
        crossorigin="anonymous"
    ></script>
    """.format(version, ".slim" if slim else "")


def bootstrapcss(version: str) -> str:
    return """
    <link
        rel="stylesheet"
        href="https://stackpath.bootstrapcdn.com/bootstrap/{0}/css/bootstrap.min.css"
        crossorigin="anonymous"
    ></link>
    <script
        src="https://stackpath.bootstrapcdn.com/bootstrap/{0}/js/bootstrap.min.js"
        crossorigin="anonymous"
    ></script>
    """.format(version)
