from typing import Union


def make_robots(
        useragent: str = "*",
        allow: str = "*",
        disallow: str = "",
        sitemap: Union[str, None] = None
):
    return """
User-agent: {0}
Allow: {1}
Disallow: {2}
{3}
    """.format(
        useragent,
        allow,
        disallow,
        "Sitemap: {}".format(
            sitemap
        ) if sitemap is not None else ""
    )
